mapper.select:
	SELECT 
		* 
	FROM 
		group_product_code 
	WHERE
		1=1
		 AND id=#{id} 
 		 AND product_id=#{productId} 
 		AND code=#{code} 
 		AND is_succeed=#{isSucceed} 
 		AND is_available=#{isAvailable}

	SELECT 
		gai.is_head AS isHead,
		gai.succeed_time AS succeedTime,
		gai.is_available AS isAvailable,
		gai.create_time AS createTime,	
		a.name,
		a.nickname,
		a.image AS image
	FROM 
		group_account_info gai,
		account a
	WHERE
		gai.account_id=a.id
		 AND gai.id=#{id} 
		 AND gai.group_product_code_id=#{groupProductCodeId} 
 		 AND gai.account_id=#{accountId} 
 		 AND gai.product_id=#{productId} 
 		AND gai.code=#{code} 
 		AND gai.is_head=#{isHead} 
 		AND gai.is_available=#{isAvailable} 
 	ORDER BY gai.is_head DESC,gai.create_time,gai.id
