mapper.update:
UPDATE activity_crazy_food_record
left_times=#{leftTimes}, used_times=#{usedTimes}, is_wine=#{isWine}, is_shared=#{isShared},
WHERE activity_id=#{activityId} AND username=#{username}

mapper.select:
SELECT
	count(id)
FROM
	red_packet_draw_record
WHERE
	1=1
	 AND share_account_id=#{shareAccountId} 
	 AND weixin_openid=#{weixinOpenid} 
	 AND mobile_number=#{mobileNumber}

mapper.update:
UPDATE
	red_packet_share_record
SET
	is_receive_coupon=#{isReceiveCoupon}
WHERE
	share_account_id=#{shareAccountId}

update activity_crazy_food_prize
probability=probability+#{probability}, is_available=#{isAvailable},
where activity_id=#{activityId} and type=#{type}
